mapper.select:
	 	SELECT service_config, project_info_config, story_config, reward_config, maker_config
        FROM project_list
        WHERE project_code = #{project_code}

mapper.update:
UPDATE project_list
SET service_config = 'Y',
	service_type = #{service_type}
WHERE project_code = #{project_code}

