mapper.insert:

	SELECT IFNULL(MAX(news_code), 0)
	FROM news

INSERT 
INTO news
VALUES (
	#{news_code} + 1
	, #{project_code}
	, #{maker_email}
	, #{news_title}
	, #{news_content}
	, NOW()
	, 'Y'
)

mapper.select:
SELECT *
FROM news
WHERE 
	project_code = #{project_code}
	AND news_status = 'Y'
ORDER BY news_date DESC
LIMIT
	#{startRow}
	, #{listLimit}

mapper.update:
UPDATE news
SET news_status = 'D'
WHERE news_code = #{news_code}

UPDATE news
SET news_title = #{news_title}
	, news_content = #{news_content}
WHERE news_code = #{news_code}

mapper.select:
SELECT SUM(payment_amount)
FROM payment
WHERE project_code = #{project_code}

SELECT COUNT(*)
FROM shipment
WHERE project_code = #{project_code}
